import React, { forwardRef, useImperativeHandle, useRef } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  RadialLinearScale,
  PieController,
  DoughnutController,
  LineController,
  BarController,
  PolarAreaController,
  RadarController,
  ScatterController,
  BubbleController,
  Title,
  Tooltip,
  Legend,
  Filler
} from 'chart.js';
import { Line, Bar, Pie, Doughnut, PolarArea, Radar, Scatter, Bubble } from 'react-chartjs-2';
import './Chart.css';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  RadialLinearScale,
  PieController,
  DoughnutController,
  LineController,
  BarController,
  PolarAreaController,
  RadarController,
  ScatterController,
  BubbleController,
  Title,
  Tooltip,
  Legend,
  Filler
);

/**
 * Chart component with Shadcn UI styling
 * 
 * @param {Object} props - Component props
 * @param {string} [props.type='line'] - Chart type (line, bar, pie, doughnut, polarArea, radar, scatter, bubble)
 * @param {Object} props.data - Chart data object
 * @param {Object} [props.options={}] - Chart options
 * @param {string} [props.className=''] - Additional CSS class names
 * @returns {JSX.Element} - Chart component
 */
const Chart = forwardRef(({
  type = 'line',
  data,
  options = {},
  className = '',
  ...props
}, ref) => {
  const chartRef = useRef(null);
  
  // Expose the chart instance to the parent component
  useImperativeHandle(ref, () => ({
    get ctx() {
      return chartRef.current?.canvas?.getContext('2d');
    },
    get chartArea() {
      return chartRef.current?.chartArea;
    },
    get chart() {
      return chartRef.current;
    }
  }));
  
  const chartClasses = ['ui-chart', `ui-chart-${type}`, className].filter(Boolean).join(' ');
  
  // Default options merged with user options
  const defaultOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      tooltip: {
        backgroundColor: 'var(--popover)',
        titleColor: 'var(--popover-foreground)',
        bodyColor: 'var(--popover-foreground)',
        borderColor: 'var(--border)',
        borderWidth: 1,
        padding: 12,
        boxPadding: 6,
        usePointStyle: true,
        bodyFont: {
          family: 'var(--font-sans)',
          size: 13
        },
        titleFont: {
          family: 'var(--font-sans)',
          size: 14,
          weight: 'bold'
        }
      }
    },
    ...options
  };

  // Render different chart types based on the type prop
  const renderChart = () => {
    switch (type) {
      case 'bar':
        return <Bar ref={chartRef} data={data} options={defaultOptions} {...props} />;
      case 'pie':
        return <Pie ref={chartRef} data={data} options={defaultOptions} {...props} />;
      case 'doughnut':
        return <Doughnut ref={chartRef} data={data} options={defaultOptions} {...props} />;
      case 'polarArea':
        return <PolarArea ref={chartRef} data={data} options={defaultOptions} {...props} />;
      case 'radar':
        return <Radar ref={chartRef} data={data} options={defaultOptions} {...props} />;
      case 'scatter':
        return <Scatter ref={chartRef} data={data} options={defaultOptions} {...props} />;
      case 'bubble':
        return <Bubble ref={chartRef} data={data} options={defaultOptions} {...props} />;
      case 'line':
      default:
        return <Line ref={chartRef} data={data} options={defaultOptions} {...props} />;
    }
  };

  return (
    <div className={chartClasses}>
      {renderChart()}
    </div>
  );
});

Chart.displayName = 'Chart';

export default Chart;
