import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Card,
  CardHeader,
  CardTitle,
  CardDescription,
  CardContent,
  Button,
  Input,
  Badge,
  Chart,
  StatisticsCard,
  Header,
  ThemeProvider
} from './ui';
import './EnhancedStockChart.css';

const EnhancedStockChart = () => {
  const [symbol, setSymbol] = useState('');
  const [marketData, setMarketData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [timeframe, setTimeframe] = useState('1m'); // 1d, 1w, 1m, 3m, 1y
  
  // Popular stocks for quick selection
  const popularStocks = [
    { symbol: 'AAPL', name: 'Apple' },
    { symbol: 'MSFT', name: 'Microsoft' },
    { symbol: 'GOOGL', name: 'Google' },
    { symbol: 'AMZN', name: 'Amazon' },
    { symbol: 'TSLA', name: 'Tesla' },
    { symbol: 'META', name: 'Meta' },
    { symbol: 'NFLX', name: 'Netflix' },
    { symbol: 'NVDA', name: 'NVIDIA' },
  ];

  const handleSubmit = async (e) => {
    if (e) e.preventDefault();
    
    if (!symbol.trim()) {
      setError('Please enter a stock symbol');
      return;
    }

    fetchStockData(symbol);
  };

  const fetchStockData = async (stockSymbol) => {
    if (!stockSymbol.trim()) return;
    
    setLoading(true);
    setError(null);

    try {
      const response = await axios.get(`/api/stock-data/${stockSymbol.toUpperCase()}`);
      setMarketData(response.data);
    } catch (err) {
      console.error('Error fetching stock data:', err);
      setError(err.response?.data?.error || 'Failed to fetch stock data');
      setMarketData(null);
    } finally {
      setLoading(false);
    }
  };

  const handleQuickSelect = (stockSymbol) => {
    setSymbol(stockSymbol);
    fetchStockData(stockSymbol);
  };

  const calculatePriceChange = () => {
    if (!marketData || !marketData.data || marketData.data.length < 2) return null;
    
    const latestPrice = parseFloat(marketData.data[marketData.data.length - 1].close);
    const firstPrice = parseFloat(marketData.data[0].close);
    
    const change = latestPrice - firstPrice;
    const percentChange = (change / firstPrice) * 100;
    
    return {
      change,
      percentChange,
      isPositive: change >= 0
    };
  };

  // Prepare chart data with gradient fills and enhanced styling
  const prepareChartData = () => {
    if (!marketData || !marketData.data || !marketData.data.length) return null;
    
    return {
      labels: marketData.data.map(item => item.date),
      datasets: [
        {
          label: 'Close Price',
          data: marketData.data.map(item => item.close),
          borderColor: 'var(--chart-primary)',
          backgroundColor: 'var(--chart-primary-transparent)',
          borderWidth: 2,
          pointRadius: 1,
          pointHoverRadius: 5,
          tension: 0.3,
          fill: false,
        },
        {
          label: 'Open Price',
          data: marketData.data.map(item => item.open),
          borderColor: 'var(--chart-secondary)',
          backgroundColor: 'var(--chart-secondary-transparent)',
          borderWidth: 2,
          pointRadius: 1,
          pointHoverRadius: 5,
          tension: 0.3,
          fill: false,
        },
        {
          label: 'High',
          data: marketData.data.map(item => item.high),
          borderColor: 'var(--chart-tertiary)',
          backgroundColor: 'var(--chart-tertiary-transparent)',
          borderWidth: 2,
          pointRadius: 0,
          pointHoverRadius: 5,
          tension: 0.3,
          fill: false,
          hidden: true
        },
        {
          label: 'Low',
          data: marketData.data.map(item => item.low),
          borderColor: 'var(--chart-quaternary)',
          backgroundColor: 'var(--chart-quaternary-transparent)',
          borderWidth: 2,
          pointRadius: 0,
          pointHoverRadius: 5,
          tension: 0.3,
          fill: false,
          hidden: true
        }
      ],
    };
  };

  // Chart options with enhanced styling and theming
  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    interaction: {
      mode: 'index',
      intersect: false,
    },
    plugins: {
      legend: {
        position: 'top',
        labels: {
          usePointStyle: true,
          pointStyleWidth: 10,
        }
      },
      title: {
        display: true,
        text: marketData ? `${marketData.symbol} Price History` : 'Stock Price History',
        font: {
          size: 16,
          weight: 'bold'
        }
      },
      tooltip: {
        callbacks: {
          label: function(context) {
            const label = context.dataset.label || '';
            const value = context.parsed.y || 0;
            return `${label}: $${value.toFixed(2)}`;
          }
        }
      }
    },
    scales: {
      y: {
        title: {
          display: true,
          text: 'Price ($)'
        },
        ticks: {
          callback: function(value) {
            return '$' + value.toFixed(2);
          }
        },
        grid: {
          drawBorder: false,
        }
      },
      x: {
        title: {
          display: true,
          text: 'Date'
        },
        grid: {
          display: false,
        }
      }
    }
  };

  // Generate data for statistics cards
  const getStatisticData = () => {
    if (!marketData || !marketData.data || !marketData.data.length) return [];
    
    const priceChange = calculatePriceChange();
    const latestData = marketData.data[marketData.data.length - 1];
    const prices = marketData.data.map(item => parseFloat(item.close));
    
    return [
      {
        title: 'Current Price',
        value: `${parseFloat(latestData.close).toFixed(2)}`,
        unit: '$',
        change: priceChange ? priceChange.percentChange : 0,
        timeframe: 'period change',
        chartData: {
          labels: marketData.data.map(item => item.date),
          datasets: [{
            data: marketData.data.map(item => item.close),
            borderColor: 'var(--chart-primary)',
            backgroundColor: 'var(--chart-primary-transparent)',
            fill: true,
            borderWidth: 2,
            tension: 0.4,
          }]
        }
      },
      {
        title: 'Trading Volume',
        value: `${Number(latestData.volume).toLocaleString()}`,
        description: 'Latest trading day',
      },
      {
        title: 'High / Low',
        value: `${parseFloat(latestData.high).toFixed(2)} / ${parseFloat(latestData.low).toFixed(2)}`,
        unit: '$',
        description: 'Latest trading day',
      },
      {
        title: 'Open Price',
        value: `${parseFloat(latestData.open).toFixed(2)}`,
        unit: '$',
        description: 'Latest trading day',
      }
    ];
  };

  return (
    <ThemeProvider>
      <div className="enhanced-stock-chart">
        <Header title="Stock Market Analytics" />
        
        <div className="enhanced-stock-content">
          <Card className="search-card">
            <CardHeader>
              <CardTitle>Stock Market Data</CardTitle>
              <CardDescription>Enter a stock symbol to view historical market data</CardDescription>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="search-form">
                <Input
                  type="text"
                  placeholder="Enter stock symbol (e.g., AAPL)"
                  value={symbol}
                  onChange={(e) => setSymbol(e.target.value)}
                />
                <Button 
                  type="submit" 
                  disabled={loading}
                >
                  {loading ? 'Loading...' : 'Get Data'}
                </Button>
              </form>
              
              <div className="timeframe-selector">
                <span className="timeframe-label">Timeframe:</span>
                {['1d', '1w', '1m', '3m', '1y'].map(tf => (
                  <Button
                    key={tf}
                    size="sm"
                    variant={timeframe === tf ? 'default' : 'outline'}
                    onClick={() => setTimeframe(tf)}
                  >
                    {tf}
                  </Button>
                ))}
              </div>

              <div className="popular-stocks">
                <div className="popular-stocks-label">Popular Stocks:</div>
                <div className="popular-stocks-buttons">
                  {popularStocks.map(stock => (
                    <Button 
                      key={stock.symbol}
                      variant="outline"
                      size="sm"
                      onClick={() => handleQuickSelect(stock.symbol)}
                      disabled={loading}
                    >
                      {stock.name}
                    </Button>
                  ))}
                </div>
              </div>

              {error && (
                <div className="error-message">
                  {error}
                </div>
              )}
            </CardContent>
          </Card>

          {marketData && marketData.data && marketData.data.length > 0 && (
            <div className="chart-section">
              <Card className="chart-card">
                <CardHeader>
                  <div className="chart-header">
                    <div>
                      <CardTitle>
                        {marketData.symbol} {marketData.name}
                      </CardTitle>
                      <CardDescription>
                        Historical price data
                      </CardDescription>
                    </div>
                    <Badge variant={calculatePriceChange()?.isPositive ? 'default' : 'destructive'}>
                      {calculatePriceChange()?.isPositive ? '↑' : '↓'} {Math.abs(calculatePriceChange()?.percentChange).toFixed(2)}%
                    </Badge>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="stock-chart-container">
                    <Chart
                      type="line"
                      data={prepareChartData()}
                      options={chartOptions}
                    />
                  </div>
                </CardContent>
              </Card>
              
              <div className="statistics-section">
                {getStatisticData().map((stat, index) => (
                  <StatisticsCard
                    key={index}
                    title={stat.title}
                    value={stat.value}
                    unit={stat.unit}
                    change={stat.change}
                    timeframe={stat.timeframe}
                    description={stat.description}
                    chartData={stat.chartData}
                  />
                ))}
              </div>
            </div>
          )}
        </div>
      </div>
    </ThemeProvider>
  );
};

export default EnhancedStockChart;
