import React, { useState } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const StockChart = () => {
  const [symbol, setSymbol] = useState('');
  const [marketData, setMarketData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!symbol.trim()) {
      setError('Please enter a stock symbol');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await axios.get(`/api/stock-data/${symbol.toUpperCase()}`);
      setMarketData(response.data);
    } catch (err) {
      console.error('Error fetching stock data:', err);
      setError(err.response?.data?.error || 'Failed to fetch stock data');
      setMarketData(null);
    } finally {
      setLoading(false);
    }
  };

  const chartData = marketData ? {
    labels: marketData.data.map(item => item.date),
    datasets: [
      {
        label: 'Close Price',
        data: marketData.data.map(item => item.close),
        borderColor: '#28a745',
        backgroundColor: 'rgba(40, 167, 69, 0.1)',
        borderWidth: 0.5,
        tension: 0.1,
        pointRadius: 0,
      },
      {
        label: 'Open Price',
        data: marketData.data.map(item => item.open),
        borderColor: '#007bff',
        backgroundColor: 'rgba(0, 123, 255, 0.1)',
        borderWidth: 0.5,
        tension: 0.1,
        pointRadius: 0,
      },
      {
        label: 'High',
        data: marketData.data.map(item => item.high),
        borderColor: '#ffc107',
        backgroundColor: 'rgba(255, 193, 7, 0.1)',
        borderWidth: 0.5,
        tension: 0.1,
        hidden: true,
        pointRadius: 0,
      },
      {
        label: 'Low',
        data: marketData.data.map(item => item.low),
        borderColor: '#dc3545',
        backgroundColor: 'rgba(220, 53, 69, 0.1)',
        borderWidth: 0.5,
        tension: 0.1,
        hidden: true,
        pointRadius: 0,
      }
    ],
  } : null;

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: marketData ? `${marketData.symbol} Price History` : 'Stock Price History',
      },
      tooltip: {
        callbacks: {
          label: function(context) {
            const label = context.dataset.label || '';
            const value = context.parsed.y || 0;
            return `${label}: $${value.toFixed(2)}`;
          }
        }
      }
    },
    scales: {
      y: {
        title: {
          display: true,
          text: 'Price ($)'
        },
        ticks: {
          callback: function(value) {
            return '$' + value.toFixed(2);
          }
        }
      },
      x: {
        title: {
          display: true,
          text: 'Date'
        }
      }
    }
  };

  // Popular stocks for quick selection
  const popularStocks = [
    { symbol: 'AAPL', name: 'Apple' },
    { symbol: 'MSFT', name: 'Microsoft' },
    { symbol: 'GOOGL', name: 'Google' },
    { symbol: 'AMZN', name: 'Amazon' },
    { symbol: 'TSLA', name: 'Tesla' },
  ];

  const handleQuickSelect = (stockSymbol) => {
    setSymbol(stockSymbol);
    // Submit the form programmatically
    handleSubmitWithSymbol(stockSymbol);
  };

  const handleSubmitWithSymbol = async (stockSymbol) => {
    if (!stockSymbol.trim()) return;
    
    setLoading(true);
    setError(null);

    try {
      const response = await axios.get(`/api/stock-data/${stockSymbol.toUpperCase()}`);
      setMarketData(response.data);
    } catch (err) {
      console.error('Error fetching stock data:', err);
      setError(err.response?.data?.error || 'Failed to fetch stock data');
      setMarketData(null);
    } finally {
      setLoading(false);
    }
  };

  const calculatePriceChange = () => {
    if (!marketData || !marketData.data || marketData.data.length < 2) return null;
    
    const latestPrice = parseFloat(marketData.data[marketData.data.length - 1].close);
    const firstPrice = parseFloat(marketData.data[0].close);
    
    const change = latestPrice - firstPrice;
    const percentChange = (change / firstPrice) * 100;
    
    return {
      change,
      percentChange,
      isPositive: change >= 0
    };
  };

  const priceChange = calculatePriceChange();

  return (
    <div style={{ 
      padding: '20px',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif'
    }}>
      <div style={{ 
        borderRadius: '8px',
        boxShadow: '0 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24)',
        overflow: 'hidden',
        marginBottom: '2rem',
        backgroundColor: '#fff'
      }}>
        <div style={{ 
          padding: '1.5rem',
          borderBottom: '1px solid #e9ecef',
          backgroundColor: '#f8f9fa'
        }}>
          <h2 style={{ 
            margin: '0 0 0.5rem 0',
            fontSize: '1.5rem',
            fontWeight: '600',
            color: '#212529'
          }}>Stock Market Data</h2>
          <p style={{ 
            margin: '0',
            fontSize: '0.875rem',
            color: '#6c757d'
          }}>Enter a stock symbol to view historical market data</p>
        </div>
        <div style={{ padding: '1.5rem' }}>
          <form onSubmit={handleSubmit} style={{ 
            display: 'flex',
            gap: '0.5rem',
            marginBottom: '1rem' 
          }}>
            <input
              type="text"
              placeholder="Enter stock symbol (e.g., AAPL)"
              value={symbol}
              onChange={(e) => setSymbol(e.target.value)}
              style={{ 
                flex: '1',
                padding: '0.5rem 0.75rem',
                fontSize: '1rem',
                borderRadius: '4px',
                border: '1px solid #ced4da',
                outline: 'none'
              }}
            />
            <button 
              type="submit" 
              style={{ 
                padding: '0.5rem 1rem',
                backgroundColor: loading ? '#6c757d' : '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                fontSize: '1rem',
                cursor: loading ? 'not-allowed' : 'pointer',
                opacity: loading ? '0.65' : '1'
              }}
              disabled={loading}
            >
              {loading ? 'Loading...' : 'Get Data'}
            </button>
          </form>

          <div style={{ marginBottom: '1rem' }}>
            <h4 style={{ 
              margin: '0 0 0.5rem 0',
              fontSize: '1rem',
              fontWeight: '600',
              color: '#212529'
            }}>Popular Stocks:</h4>
            <div style={{ 
              display: 'flex',
              flexWrap: 'wrap',
              gap: '0.5rem'
            }}>
              {popularStocks.map(stock => (
                <button 
                  key={stock.symbol}
                  onClick={() => handleQuickSelect(stock.symbol)}
                  style={{ 
                    padding: '0.375rem 0.75rem',
                    border: '1px solid #007bff',
                    borderRadius: '4px',
                    backgroundColor: 'transparent',
                    color: '#007bff',
                    cursor: loading ? 'not-allowed' : 'pointer',
                    opacity: loading ? '0.65' : '1',
                    fontSize: '0.875rem'
                  }}
                  disabled={loading}
                >
                  {stock.name}
                </button>
              ))}
            </div>
          </div>

          {error && (
            <div style={{ 
              backgroundColor: 'rgba(239, 68, 68, 0.1)', 
              color: '#dc3545',
              borderRadius: '4px',
              padding: '0.75rem 1rem',
              marginBottom: '1rem'
            }}>
              {error}
            </div>
          )}

          {marketData && marketData.data && marketData.data.length > 0 && (
            <div style={{ width: '100%' }}>
              <Line data={chartData} options={chartOptions} />
              
              <div style={{ 
                marginTop: '1.5rem',
                padding: '1.5rem',
                borderRadius: '8px',
                border: '1px solid #e9ecef',
                backgroundColor: '#f8f9fa'
              }}>
                <h3 style={{ 
                  margin: '0 0 1rem 0',
                  fontSize: '1.25rem',
                  fontWeight: '600',
                  color: '#212529'
                }}>Latest Data ({marketData.data[marketData.data.length - 1].date})</h3>
                
                {priceChange && (
                  <div style={{ 
                    textAlign: 'center',
                    marginBottom: '1rem'
                  }}>
                    <span style={{ 
                      display: 'inline-block',
                      padding: '0.5rem 1rem',
                      borderRadius: '4px',
                      backgroundColor: priceChange.isPositive ? 'rgba(34, 197, 94, 0.1)' : 'rgba(239, 68, 68, 0.1)',
                      color: priceChange.isPositive ? '#198754' : '#dc3545',
                      fontWeight: 500
                    }}>
                      {priceChange.isPositive ? '↑' : '↓'} ${Math.abs(priceChange.change).toFixed(2)} ({priceChange.percentChange.toFixed(2)}%)
                    </span>
                  </div>
                )}
                
                <div style={{ 
                  display: 'grid',
                  gridTemplateColumns: 'repeat(2, 1fr)',
                  gap: '1rem',
                  '@media (min-width: 768px)': {
                    gridTemplateColumns: 'repeat(5, 1fr)',
                  }
                }}>
                  <div style={{ 
                    borderRadius: '4px',
                    backgroundColor: '#fff',
                    border: '1px solid #e9ecef',
                    padding: '1rem',
                    textAlign: 'center'
                  }}>
                    <div style={{ fontSize: '0.875rem', color: '#6c757d', marginBottom: '0.25rem' }}>Open</div>
                    <div style={{ fontSize: '1.25rem', fontWeight: 600 }}>${Number(marketData.data[marketData.data.length - 1].open).toFixed(2)}</div>
                  </div>
                  <div style={{ 
                    borderRadius: '4px',
                    backgroundColor: '#fff',
                    border: '1px solid #e9ecef',
                    padding: '1rem',
                    textAlign: 'center'
                  }}>
                    <div style={{ fontSize: '0.875rem', color: '#6c757d', marginBottom: '0.25rem' }}>Close</div>
                    <div style={{ fontSize: '1.25rem', fontWeight: 600 }}>${Number(marketData.data[marketData.data.length - 1].close).toFixed(2)}</div>
                  </div>
                  <div style={{ 
                    borderRadius: '4px',
                    backgroundColor: '#fff',
                    border: '1px solid #e9ecef',
                    padding: '1rem',
                    textAlign: 'center'
                  }}>
                    <div style={{ fontSize: '0.875rem', color: '#6c757d', marginBottom: '0.25rem' }}>High</div>
                    <div style={{ fontSize: '1.25rem', fontWeight: 600 }}>${Number(marketData.data[marketData.data.length - 1].high).toFixed(2)}</div>
                  </div>
                  <div style={{ 
                    borderRadius: '4px',
                    backgroundColor: '#fff',
                    border: '1px solid #e9ecef',
                    padding: '1rem',
                    textAlign: 'center'
                  }}>
                    <div style={{ fontSize: '0.875rem', color: '#6c757d', marginBottom: '0.25rem' }}>Low</div>
                    <div style={{ fontSize: '1.25rem', fontWeight: 600 }}>${Number(marketData.data[marketData.data.length - 1].low).toFixed(2)}</div>
                  </div>
                  <div style={{ 
                    borderRadius: '4px',
                    backgroundColor: '#fff',
                    border: '1px solid #e9ecef',
                    padding: '1rem',
                    textAlign: 'center'
                  }}>
                    <div style={{ fontSize: '0.875rem', color: '#6c757d', marginBottom: '0.25rem' }}>Volume</div>
                    <div style={{ fontSize: '1.25rem', fontWeight: 600 }}>{Number(marketData.data[marketData.data.length - 1].volume).toLocaleString()}</div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default StockChart;
