import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useTheme } from './ui/ThemeProvider';

const LiveFeed = () => {
  const [analystRatings, setAnalystRatings] = useState([]);
  const [headlines, setHeadlines] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { theme } = useTheme();
  
  // Determine if using dark mode
  const isDarkMode = theme === 'dark' || (theme === 'system' && window.matchMedia('(prefers-color-scheme: dark)').matches);
  
  // Theme-based colors
  const colors = {
    text: isDarkMode ? '#e4e4e7' : '#212529',
    mutedText: isDarkMode ? '#a1a1aa' : '#6c757d',
    background: isDarkMode ? '#18181b' : '#fff',
    cardBackground: isDarkMode ? '#1a1a1a' : '#fff',
    cardHeaderBackground: isDarkMode ? '#1a1a1a' : '#f8f9fa',
    border: isDarkMode ? '#3f3f46' : '#e9ecef',
    primary: '#007bff',
    primaryDark: '#0056b3',
    buttonText: '#fff',
    dangerText: isDarkMode ? '#f87171' : '#dc3545',
    successText: isDarkMode ? '#4ade80' : '#198754',
  };

  // Function to fetch analyst ratings
  const fetchAnalystRatings = async () => {
    try {
      const response = await axios.get('/api/analyst-ratings/5');
      if (response.data && response.data.success && response.data.data) {
        setAnalystRatings(response.data.data);
      }
    } catch (err) {
      console.error('Error fetching analyst ratings:', err);
      setError('Failed to fetch analyst ratings');
    }
  };

  // Function to fetch headlines
  const fetchHeadlines = async () => {
    try {
      const response = await axios.get('/api/headlines/5');
      if (response.data && response.data.success && response.data.data) {
        setHeadlines(response.data.data);
      }
    } catch (err) {
      console.error('Error fetching headlines:', err);
      setError('Failed to fetch headlines');
    } finally {
      setLoading(false);
    }
  };

  // Fetch data on component mount and set up interval
  useEffect(() => {
    const fetchData = async () => {
      await Promise.all([fetchAnalystRatings(), fetchHeadlines()]);
    };

    // Initial fetch
    fetchData();

    // Set up interval to fetch data every 8 seconds
    const intervalId = setInterval(fetchData, 8000);

    // Clean up interval on component unmount
    return () => clearInterval(intervalId);
  }, []);

  // Format date for display
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric' 
    });
  };

  return (
    <div style={{
      padding: '0 20px 20px 20px', // Reduced top padding
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif',
      color: colors.text,
      backgroundColor: 'transparent'
    }}>
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(2, 1fr)',
        gap: '1rem',
        width: '100%',
        height: '400px' // Fixed height to match the main chart
      }}>
        {/* Headlines Column (NOW ON LEFT) */}
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          height: '100%' // Full height
        }}>
          <div style={{
            borderRadius: '8px',
            overflow: 'hidden',
            backgroundColor: colors.background,
            boxShadow: isDarkMode ? '0 1px 3px rgba(0,0,0,0.3)' : '0 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24)',
            display: 'flex',
            flexDirection: 'column',
            height: '100%' // Full height
          }}>
            <div style={{
              padding: '1rem',
              borderBottom: `1px solid ${colors.border}`,
              backgroundColor: colors.cardHeaderBackground
            }}>
              <h3 style={{
                margin: '0 0 0 0',
                fontSize: '1.5rem',
                fontWeight: '600',
                color: colors.text
              }}>Market Headlines</h3>
              <p style={{
                margin: '0',
                fontSize: '0.875rem',
                color: colors.mutedText
              }}>Latest news refreshed every 8 seconds</p>
            </div>

            <div style={{ 
              padding: '1rem',
              overflowY: 'auto', // Enable scrolling
              flex: '1' // Take remaining space
            }}>
              {loading ? (
                <p style={{ color: colors.mutedText }}>Loading headlines...</p>
              ) : error ? (
                <p style={{ color: colors.dangerText }}>{error}</p>
              ) : (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
                  {headlines.map(headline => (
                    <div 
                      key={headline.id}
                      style={{
                        padding: '1rem',
                        borderRadius: '8px',
                        backgroundColor: colors.cardBackground,
                        border: `1px solid ${colors.border}`,
                        boxShadow: isDarkMode ? '0 1px 2px rgba(0,0,0,0.2)' : '0 1px 2px rgba(0,0,0,0.05)'
                      }}
                    >
                      <div style={{ 
                        display: 'flex', 
                        alignItems: 'flex-start', 
                        justifyContent: 'space-between' 
                      }}>
                        <div style={{ marginBottom: '0.5rem' }}>
                          <span style={{ 
                            backgroundColor: '#ff6b6b', 
                            color: colors.buttonText,
                            padding: '0.25rem 0.5rem',
                            borderRadius: '4px',
                            fontSize: '0.75rem',
                            fontWeight: '600',
                            marginRight: '0.5rem'
                          }}>
                            {headline.stock}
                          </span>
                          <span style={{ 
                            color: colors.mutedText,
                            fontSize: '0.75rem'
                          }}>
                            {formatDate(headline.date)}
                          </span>
                        </div>
                        {headline.publisher && (
                          <span style={{ 
                            color: colors.mutedText,
                            fontSize: '0.75rem',
                            fontStyle: 'italic'
                          }}>
                            {headline.publisher}
                          </span>
                        )}
                      </div>
                      <p style={{ margin: '0.5rem 0 0 0', fontSize: '0.9rem' }}>
                        {headline.headline}
                      </p>
                      {headline.url && (
                        <a 
                          href={headline.url} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          style={{
                            display: 'inline-block',
                            marginTop: '0.5rem',
                            fontSize: '0.8rem',
                            color: '#ff6b6b',
                            textDecoration: 'none'
                          }}
                        >
                          Read more →
                        </a>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Analyst Ratings Column (NOW ON RIGHT) */}
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          height: '100%' // Full height
        }}>
          <div style={{
            borderRadius: '8px',
            overflow: 'hidden',
            backgroundColor: colors.background,
            boxShadow: isDarkMode ? '0 1px 3px rgba(0,0,0,0.3)' : '0 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24)',
            display: 'flex',
            flexDirection: 'column',
            height: '100%' // Full height
          }}>
            <div style={{
              padding: '1rem',
              borderBottom: `1px solid ${colors.border}`,
              backgroundColor: colors.cardHeaderBackground
            }}>
              <h3 style={{
                margin: '0 0 0 0',
                fontSize: '1.5rem',
                fontWeight: '600',
                color: colors.text
              }}>Analyst Ratings</h3>
              <p style={{
                margin: '0',
                fontSize: '0.875rem',
                color: colors.mutedText
              }}>Latest ratings refreshed every 8 seconds</p>
            </div>

            <div style={{ 
              padding: '1rem',
              overflowY: 'auto', // Enable scrolling
              flex: '1' // Take remaining space
            }}>
              {loading ? (
                <p style={{ color: colors.mutedText }}>Loading analyst ratings...</p>
              ) : error ? (
                <p style={{ color: colors.dangerText }}>{error}</p>
              ) : (
                <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
                  {analystRatings.map(rating => (
                    <div 
                      key={rating.id}
                      style={{
                        padding: '1rem',
                        borderRadius: '8px',
                        backgroundColor: colors.cardBackground,
                        border: `1px solid ${colors.border}`,
                        boxShadow: isDarkMode ? '0 1px 2px rgba(0,0,0,0.2)' : '0 1px 2px rgba(0,0,0,0.05)'
                      }}
                    >
                      <div style={{ 
                        display: 'flex', 
                        alignItems: 'flex-start', 
                        justifyContent: 'space-between' 
                      }}>
                        <div style={{ marginBottom: '0.5rem' }}>
                          <span style={{ 
                            backgroundColor: colors.primary, 
                            color: colors.buttonText,
                            padding: '0.25rem 0.5rem',
                            borderRadius: '4px',
                            fontSize: '0.75rem',
                            fontWeight: '600',
                            marginRight: '0.5rem'
                          }}>
                            {rating.stock}
                          </span>
                          <span style={{ 
                            color: colors.mutedText,
                            fontSize: '0.75rem'
                          }}>
                            {formatDate(rating.date)}
                          </span>
                        </div>
                        {rating.publisher && (
                          <span style={{ 
                            color: colors.mutedText,
                            fontSize: '0.75rem',
                            fontStyle: 'italic'
                          }}>
                            {rating.publisher}
                          </span>
                        )}
                      </div>
                      <p style={{ margin: '0.5rem 0 0 0', fontSize: '0.9rem' }}>
                        {rating.headline}
                      </p>
                      {rating.url && (
                        <a 
                          href={rating.url} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          style={{
                            display: 'inline-block',
                            marginTop: '0.5rem',
                            fontSize: '0.8rem',
                            color: colors.primary,
                            textDecoration: 'none'
                          }}
                        >
                          Read more →
                        </a>
                      )}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LiveFeed;
